<!DOCTYPE html>
<html lang="en">
<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>Recherche Google Maps</title>
    <style>
        #map {
            height: 400px;
            width: 100%;
        }
    </style>
</head>
<body>
    <input id="pac-input" type="text" placeholder="Rechercher">
    <div id="map"></div>

    <script>
        function initMap() {
            // Coordonnées géographiques du centre de la carte (par exemple, Paris).
            var center = { lat: 48.8566, lng: 2.3522 };

            // Créer une instance de carte Google Maps.
            var map = new google.maps.Map(document.getElementById('map'), {
                center: center,
                zoom: 12
            });

            // Créer une instance de l'objet de recherche.
            var input = document.getElementById('pac-input');
            var searchBox = new google.maps.places.SearchBox(input);

            // Associer la zone de recherche à la carte actuelle.
            map.controls[google.maps.ControlPosition.TOP_LEFT].push(input);

            // Mettre à jour la carte en fonction des résultats de la recherche.
            map.addListener('bounds_changed', function () {
                searchBox.setBounds(map.getBounds());
            });

            // Répondre aux changements de sélection dans la zone de recherche.
            searchBox.addListener('places_changed', function () {
                var places = searchBox.getPlaces();

                if (places.length === 0) {
                    return;
                }

                // Centrer la carte sur le lieu sélectionné.
                map.setCenter(places[0].geometry.location);
                map.setZoom(15);
            });
        }
    </script>

    <!-- Charger l'API Google Maps avec votre clé API -->
    <script src="https://maps.googleapis.com/maps/api/js?key=VOTRE_CLE_API&libraries=places&callback=initMap" async defer></script>
</body>
</html>